package conditionalStatements;

public class SwitchDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int score =110;
		
		switch(score)
		{
		case 0: System.out.println("Duck out"); break;
		case 50: System.out.println("Half Century"); break;
		case 100: System.out.println("Century"); break;
		case 200: System.out.println("Double Century"); break;
		default: System.out.println("Runs");
		}
		
		

	}


	}


