package nestedLoops;

public class Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=1; i<=10; i++)  //outer loop  i=4 < = 3 F
		{
			for (int j=1; j<=i; j++) //inner loop j=4 <=3 T
			{
				System.out.print(" "+"*"+" ");  
			}                        
			                          
			System.out.println();
		}

	}


	}


