package nestedLoops;

public class Rainbow {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String colors = "pink";
		
		switch(colors)
		{
		case "voilet": System.out.println("Rainbow color"); break;
		case "indigo": System.out.println("Rainbow color"); break;
		case "blue"  : System.out.println("Rainbow color"); break;
		case "green" : System.out.println("Rainbow color"); break;
		case "yellow": System.out.println("Rainbow color"); break;
		case "orange": System.out.println("Rainbow color"); break;
		case "red"   : System.out.println("Rainbow color"); break;
		default      : 	System.out.println("Not a Rainbow color");
			
		}

	

	}

}
